mapper.select:
select file_id, file_name, file_size, file_ext, file_user_id, file_uuid, file_path, file_state, file_class_id, file_create_time,file_brief,user_name,classification_name from file_info f left join user_info u on f.file_user_id = u.user_id left join classification c on f.file_class_id = c.classification_id where f.file_state = 5 and f.file_class_id in (
( #{obj.classificationId} )
)

mapper.update:
update file_info
set
file_state = #{fileState}
where
file_uuid = #{fileUuid}
